function isSameResource(urlOne, urlTwo) { var fragmentPattern = /#.*$/; var resourceOne = urlOne.replace(fragmentPattern, ''); var resourceTwo = urlTwo.replace(fragmentPattern, ''); return resourceOne === resourceTwo;
}

function getFragmentTarget(id) { if (id.slice(0, 1) === '#') { id = id.slice(1); } // we're looking for <div id="{id}"> or <a name="{id}">
    return document.getElementById(id) || document.querySelector('a[name="' + id + '"]');
}

// handle activation of all fragment links on the current page
$(document.body).on('click', 'a[href*="#"]:not([href="#"])', function(event) { if (event.isDefaultPrevented()) { // some other event handler might have already handled this event
        return; } if (!isSameResource(location.href, this.href)) { return; } var target = getFragmentTarget(this.hash); if (!target) { return; } // prevent browser from jumping to the fragment
    // because we want to scroll it into view first
    event.preventDefault(); // smooth scroll document to target element over 1 second,
    // not using Element.scrollIntoView() because not widely supported yet
    $('html, body').animate( { scrollTop: $(target).offset().top }, 1000,       function() { // now that we’ve scrolled the target into view,
            // let the browser do its regular thing and update the URL
            window.location.hash = target.id || target.name; } );
});

function isSameResource(urlOne, urlTwo) {
var fragmentPattern = /#._$/;
var resourceOne = urlOne.replace(fragmentPattern, '');
var resourceTwo = urlTwo.replace(fragmentPattern, '');
return resourceOne === resourceTwo;
}
function getFragmentTarget(id) {
if (id.slice(0, 1) === '#') {
I'd = id.slice(1);
}
// <div id="{id}">または<a name="{id}">を探す
return document.getElementById(id)
|| document.querySelector('aname="' + I'd + '"]');
}
// 現在のページのすべての？？フラグメントリンク？？のアクティブ化を処理【コメント：定訳が確認できませんでした。原文「fragment links」】
$(document.body).on('click', 'a[href*="#"]:not([href="#"])', function(event) {
if (event.isDefaultPrevented()) {
// このイベントはすでに他のイベントハンドラーによって処理されているかもしれない
return;
}
if (!isSameResource(location.href, this.href)) {
return;
}
var target = getFragmentTarget(this.hash);
if (!target) {
return;
}
// はじめにスクロール表示させたいため、
// フラグメントにジャンプしないようにする
event.preventDefault();
// ドキュメントからターゲット要素へ1秒かけてスムーズスクロールさせる
// Element.scrollIntoView()はまだ広くサポートされていないため使わない
$('html, body').animate(
{ scrollTop: $(target).offset().top },
1000, function() {
// ターゲットがスクロール表示されるようになったので、
// 後はブラウザの通常の動作とURLの更新が行われるように設定する
window.location.hash = target.id || target.name;
}
);
});
